import React, { Component } from "react";

class Contador extends Component {

    state = {
        numero: TransitionEvent.props.numero
    }

    render() {
        return (
            <div>
                <h2>Contador</h2>
                <p>{this.state.numero}</p>

            </div>
        )
    }
}

export default Contador;

