import "./Input.css";
import React, { useState } from "react";

export default (props) => {
    const [valor, setValor] = useState('Inicial')

    function quandoMudar(e) {
        setValor(e.target.value)
    }

    return (
        <div className="Input">
            <h2>{valor}</h2>

            <input value={valor}/>
            <input onChange={quandoMudar} />
        </div>
    );
};

//target permite que você detecte quando uma função foi chamada.

//onChange é utilizado para que seja realizada determinada ação após alguma mudança.

//Readonly envia o valor para o formulário e também não pode editar.
